let PicsNames=document.querySelectorAll(".painting__name"),PicsAuthor=document.querySelectorAll(".painting__author");fetch("data/data.json").then(e=>e.json()).then(e=>{for(let t=0;t<e.length;t++)PicsNames[t].insertAdjacentText("beforeend",e[t].name),PicsAuthor[t].insertAdjacentText("beforeend",e[t].artist.name)}).catch(e=>{console.log(e.data)});